import React, { useState } from 'react';
import '../styles/Login_page.css';

const LoginPage = () => {
  const [formData, setFormData] = useState({ username: '', password: '' });
  const [errorMessage, setErrorMessage] = useState('');

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (event) => {
    
    event.preventDefault();
    const url = "http://localhost:5000/signup";
    const response = await fetch("http://localhost:5000/check_user", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    });
    console.log("hi");
    const result = await response.json();
    if (result.exists) {
      console.log('User exists');
      // Proceed with login or navigate to homepage
      window.location.href = '/homepage.html'; // Example of navigation
    } else {
      setErrorMessage('User does not exist or incorrect password');
    }
  };

  return (
    <div className="login-body">
      <div className="login-header">
        <a className="login-logo">Heart Med</a>
      </div>
      <div className="login-container">
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={formData.username}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
          />
          {errorMessage && <h2 style={{ fontSize: '20px', textAlign: 'left', color: 'red' }}>{errorMessage}</h2>}
          <button className="login-button" type="submit">Login</button>
        </form>
        <h3>
          Not a member?{' '}
          <a style={{ color: 'white', textDecoration: 'none' }} href="/Signup">
            Signup
          </a>
        </h3>
      </div>
    </div>
  );
};

export default LoginPage;
